{"filter":false,"title":"configuration.php","tooltip":"/Classes/configuration.php","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":44,"column":2},"action":"insert","lines":["<?php","// this class imports data for database","// and site configuration","//located in the /configuration folder","class Configuration{","  private $dbconfiguration = array();","  private $siteconfiguration = array();","  private $errors = array();","  ","  //configuration files location","  private $dbConfigFile = \"configuration/dbconfiguration.json.php\";","  private $siteConfigFile = \"configuration/siteconfiguration.json.php\";","  ","  public function __construct(){","    //check if configuration file exists and readable","    if( !is_readable($dbConfigFile) ){","      $this->errors[\"database\"] = \"no database configuration found\";","    }","    if( !is_readable($siteConfigFile) ){","      $this->errors[\"site\"] = \"no site configuration found\";","    }","    //if no errors read the config files","    if( count($this->errors)==0 ){","      $this->readConfigurations();","    }","    else{","      return false;","    }","  }","  ","  private function readConfigurations(){","    include_once($this->dbConfigFile);","    $dbconfig = json_decode($dbconfiguration,true);","    ","    include_once($this->siteConfigFile);","    $siteconfig = json_decode($siteconfiguration,true);","    ","    return (object) [$dbconfig,$siteconfig];","  }","  ","  public getErrors(){","    return $this->errors;","  }","}","?>"],"id":1}]]},"ace":{"folds":[],"scrolltop":113,"scrollleft":0,"selection":{"start":{"row":10,"column":13},"end":{"row":10,"column":13},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1491885464637,"hash":"231336252a5f604970b0f9f7a815d475a2d29f1b"}